    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Empleado empleado = new Empleado();
        ControladorEmpleado controlEmpleado = new ControladorEmpleado();

        if (!txt_nombre.getText().isEmpty() && !txt_apellido.getText().isEmpty() && !txt_dni.getText().isEmpty()) {
            //JOptionPane.showMessageDialog(null, "Correcto");

            if (!controlEmpleado.existeEmpleado(txt_dni.getText().trim())) {

                empleado.setNombre(txt_nombre.getText().trim());
                empleado.setApellido(txt_apellido.getText().trim());
                empleado.setDni(txt_dni.getText().trim());
                empleado.setTelefono(txt_telefono.getText().trim());
                empleado.setDireccion(txt_direccion.getText().trim());
                empleado.setCargo(txt_cargo.getText().trim());
                empleado.setEstado(1);

                if (controlEmpleado.guardar(empleado)) {
                    JOptionPane.showMessageDialog(null, "Registro Guardado");
                    
                } else {
                    JOptionPane.showMessageDialog(null, "Error al Guardar");
                }

            } else {
                JOptionPane.showMessageDialog(null, "El cliente ya esta registrado en la Base de Datos.");
                txt_nombre.setBackground(Color.white);
                txt_apellido.setBackground(Color.white);
                txt_dni.setBackground(Color.white);
                txt_telefono.setBackground(Color.white);
                txt_direccion.setBackground(Color.white);
            }
        } else {
            JOptionPane.showMessageDialog(null, "Completa todos los campos");
            
        }
        //metodo limpiar
        this.Limpiar();

    }//GEN-LAST:event_jButton1ActionPerformed

